CREATE TABLE `gram` (
`id` INT NOT NULL,
`content` TEXT NULL,
`course` CHAR(50) NULL,
`lesson` INT NULL,
PRIMARY KEY (`id`)
)
COLLATE='utf8_general_ci'
ENGINE=InnoDB
AUTO_INCREMENT=1
;

ALTER TABLE `gram`
CHANGE COLUMN `id` `id` INT(11) NOT NULL AUTO_INCREMENT FIRST;

CREATE TABLE `volca` (
`id` INT NOT NULL,
`content` TEXT NULL,
`course` CHAR(50) NULL,
`lesson` INT NULL,
PRIMARY KEY (`id`)
)
COLLATE='utf8_general_ci'
ENGINE=InnoDB
AUTO_INCREMENT=1
;
ALTER TABLE `volca`
CHANGE COLUMN `id` `id` INT(11) NOT NULL AUTO_INCREMENT FIRST;

