INSERT INTO `hoctiengnhat`.`volca` (`content`, `course`, `lesson`) VALUES ('みます(mimasu)：xem ,khám bệnh\r\nさがします(sagashimasu)：tìm,tìm kiếm\r\nおくれます(okuremasu)：chậm,muộn\r\nやります(yarimasu)：làm\r\nさんかします(sankashimasu)：tham gia,dự\r\nもうしこみます(moushikomimasu)：đăng ký\r\nつごうがいい(tsugougaii)：thuận tiện\r\nつごうがわるい(tsugougawarui)：không thuận tiện\r\nきぶんがいい(kibungaii)：cảm thấy tốt,cảm thấy khỏe\r\nきぶんがわるい(kibungawarui)：cảm thấy không tốt,cảm thấy mệt\r\nしんぶんしゃ(shinbunsha)：công ty phát hành báo,tòa soạn báo\r\nじゅうどう(jyuudou)：judo(nhu đạo)\r\nうんどうかい(undoukai)：hội thi thể thao\r\nばしょ(basho)：địa điểm\r\nボランテイア(boranteia)：tình nguyện viên\r\n―べん(–ben)：tiếng–,giọng–\r\nこんど(kondo)：lần tới\r\nずいぶん(zuibun)：khá,tương đối\r\nちょくせつ(chokusetsu)：trực tiếp\r\nいつでも(itsudemo)：lúc nào cũng\r\nどこでも(dokodemo)：ở đâu cũng\r\nだれでも(daredemo)：ai cũng\r\nなんでも(nandemo)：cái gì cũng\r\nこんなー(konna–)：–như thế này\r\nそんなー(sonna–)：–như thế đó\r\nあんなー(anna–)：–như thế kia\r\nこどものひ(kodomonohi)：ngày trẻ em\r\nかたづきます(katadukimasu)：được dọn dẹp ngăn nắp,gọn gàng\r\nごみ(gomi)：rác\r\nだします(dashimasu)：đổ ,để(rác)\r\nもえます(moemasu)：cháy được\r\nげつ、すい、きん(getsu,sui,kin)：thứ hai,thứ tư,thứ sáu\r\nおきば(okiba)：nơi để\r\nよこ(yoko)：bên cạnh\r\nびん(bin)：cái chai\r\nかん(kan)：cái lon,hộp kim loại\r\nおゆ(oyu)：nước nóng\r\nガス(gasu)：ga\r\nれんらくします(renrakushimasu)：liên lạc\r\nこまったなあ(komattanaa)：làm thế nào đây\r\nでんしメール(denshimeeru)：thư điện tử,e-mail\r\nうちゅう(uchuu)：vũ trụ\r\nこわい(kowai)：sợ\r\nうちゅうせん(uchuusen)：tàu vũ trụ\r\nべつの(betsuno)：khác\r\nうちゅうひこうし(uchuuhikoushi)：nhà du hành vũ trụ', 'n4', 26);
INSERT INTO `hoctiengnhat`.`volca` (`content`, `course`, `lesson`) VALUES ('かいます(kaimasu)：nuôi (động vật)\r\nたてます(tatemasu)：xây, xây dựng\r\nはしります(hashirimasu)：chạy (trên đường)\r\nとります(torimasu)：xin\r\nみえます(miemasu)：nhìn thấy, có thể nhìn thấy\r\nきこえます(kikoemasu)：có thể nghe thấy\r\nできます(dekimasu)；hoàn thành\r\nひらきます(hirakimasu)：mở\r\nペット(petto)：động vật cảnh\r\nとり(tori)：chim\r\nこえ(koe)：tiếng nói, giọng nói\r\nなみ(nami)：sóng\r\nはなび(hanabi)：pháo hoa\r\nけしき(keshiki)：phong cảnh\r\nひるま(hiruma)：thời gian ban ngày\r\nむかし(mukashi)：ngày xưa, trước đây\r\nどうぐ(dougu)：dụng cụ, công cụ\r\nじどうはんばいき(jidouhambaiki): máy bán tự động\r\nつうしんはんばい(tsuushinhambai)：thương mại viễn thông\r\nクリーニング(kuriiningu)：giặt ủi\r\nマンション(manshon)：nhà chung cư\r\nだいどころ(daidokoro)：bếp\r\nパーテイールーム(paateiiruumu)：phòng tiệc\r\nほかの(hokano)：khác\r\nはっきり(hakkiri)：rõ ràng\r\nほとんど(hotondo)：hầu hết, hầu như\r\nかんさいくうこう(kansaikuukou)：sân bay quốc tế\r\nあきはばら(akihabara)：tên một khu phố bán đồ điện tử nổi tiếng ở Tokyo\r\nいず(izu)：một bán đảo thuộc tỉnh Shizuoka\r\nにちようだいく(nichiyoudaiku)：làm thợ mộc chủ nhật\r\nほんだな(hondana)：giá sách\r\nゆめ(yume)：giấc mơ\r\nいつか(itsuka): một ngày nào đó,một lúc nào đó\r\nいえ(ie)：nhà\r\nすばらしい(subarashii)：tuyệt vời\r\nこどもたち(kodomotachi)：trẻ em,trẻ con\r\nだいすき(daisuki)：rất thích\r\nまんが(mangga)：truyện tranh\r\nしゅじんこう(shujinkou)：nhân vật chính\r\nかたち(katachi)：hình,dạng\r\nロボット(robotto)：người máy,rôbôt\r\nふしぎ(fushigi)：bí ẩn ,kì thú\r\nポケット(poketto)：túi áo,túi quần\r\nたとえば(tatoeba)：ví dụ\r\nつけます(tsukemasu)：lắp ghép\r\nじゆうに(jiyuuni)：tự do,tùy thích\r\nそら(sora)：bầu trời\r\nとびます(tobimasu)：bay\r\nじぶん(jibun)：bản thân,mình\r\nしょうらい(shourai)：tương lai\r\nドラえもん(doraemon)：tên một nhân vật trong phim hoạt hình (đôrêmon)', 'n4', 27);
INSERT INTO `hoctiengnhat`.`volca` (`content`, `course`, `lesson`) VALUES ('うれます(uremasu)：bán chạy,được bán\r\nおどります(odorimasu)：nhảy,khiêu vũ\r\nかみます(kamimasu)：nhai\r\nえらびます(erabimasu)：chọn\r\nちがいます(chigaimasu)：khác\r\nかよいます(kayoimasu)：đi đi về về\r\nメモします(memoshimasu)：ghi chép\r\nまじめ(hajime)：nghiêm túc,nghiêm chỉnh\r\nねっしん(nesshin)：nhiệt tâm,nhiệt tình,hết lòng\r\nやさしい(yasashii)：hiền lành\r\nえらい(erai)：vĩ đại,đáng kính\r\nちょうどいい(choudoii)：vừa đủ,vừa đúng\r\nしゅうかん(shuukan)：tập quán\r\nけいけん(keiken)：kinh nghiệm\r\nちから(chikara)：sức lực,năng lực\r\nにんきがあります(ninkigaarimasu)：sự hâm mộ\r\nかたち(katachi)：hình dáng\r\nいろ(iro)：màu\r\nあじ(aji)：vị\r\nガム(gamu)：kẹo cao su\r\nしなもの(shinamono)：hàng hóa,mặt hàng\r\nねだん(nedan)：giá cả\r\nきゅうりょう(kyuuryou)：lương\r\nボーナス(boonasu)：thưởng\r\nばんぐみ(bangumi)：chương trình\r\nドラマ(doramu): kịch ,phim truyền hình\r\nしょうせつ(shousetsu)：tiểu thuyết\r\nしょうせつか(shousetsuka)：nhà văn,tiểu thuyết gia\r\nかしゅ(kashu)：ca sĩ\r\nかんりにん(kanrinin)： người quản lí\r\nむすこ(musuko)：con trai(dùng cho mình)\r\nむすこさん(musukosan)：con trai(dùng cho người khác)\r\nむすめ(musume)：con gái(dùng cho mình)\r\nむすめさん(musumesan)：con gái(dùng cho người khác)\r\nしばらく(shibaraku)：một khoảng thời gian ngắn\r\nたいてい(taitei)：thường ,thông thường\r\nそれに(soreni)：thêm nữa là,thêm vào đó là\r\nそれで(sorede)：thế thì,thế nên\r\nおねがいがあるんですが(onegaigaarundesuga)：tôi có việc muốn nhờ anh chị\r\nホームステイ(hoomusutei)：homestay\r\nおしゃべりします(oshaberishimasu)：nói chuyện ,tán chuyện\r\nおしらせ(oshirase)；thông báo\r\nひにち(hinichi)：ngày\r\nど(do)：thứ bảy\r\nたいいくかん(taiikukan)：nhà tập,nhà thi đấu thể thao\r\nむりょう(muryou)：miễn phí', 'n4','28');
INSERT INTO `hoctiengnhat`.`volca` (`content`, `course`, `lesson`) VALUES ('あきます(akimasu)：mở (cửa)\r\nしまります(shimarimasu)：đóng (cửa)\r\nつきます(tsukimasu)：bật (điện)\r\nきえます(kiemasu)：tắt (điện)\r\nこみます(komimasu)：đông , tắc (đường)\r\nすきます(sukimasu)：vắng , thoáng (đường)\r\nこわれます(kowaremasu)：hỏng\r\nわれます(waremasu)：vỡ\r\nおれます(oremasu)： gãy\r\nやぶれます(yaburemasu)： rách\r\nよごれます(yogoremasu)：bẩn\r\nつきます(tsukimasu)：gắn, kèm theo\r\nはずれます(hazuremasu)：tuột, bung\r\nとまります(tomarimasu)：dừng\r\nまちがえます(machigaemasu)：nhầm , sai\r\nおとします(otoshimasu)：đánh rơi\r\nかかります(kakarimasu)：khóa(chìa khóa)\r\nおさら(osara)：cái đĩa\r\nおちゃわん(ochawan)： cái bát\r\nコップ(koppu)： cái cốc\r\nガラス(garasu)： thủy tinh\r\nふくろ(fukuro)： cái túi\r\nさいふ(saifu)：cái ví\r\nえだ(eda)： cành cây\r\nえきいん(ekiin)： nhân viên nhà ga\r\nこのへん(konohen)： xung quanh đây,gần đây\r\n―へん(–hen)： xung quanh\r\nこのぐらい(konogurai)：khoảng ngần này, cỡ khoảng như thế này\r\nおさきにどうぞ(osakinidouzo)：xin mời anh, chị đi trước\r\nよかった(yokatta)： ồ may quá\r\nいまのでんしゃ(imanodensha)：đoàn tàu vừa rồi\r\nわすれもの(wasuremono)：vật để quên\r\nポケット(poketto)：túi\r\nおぼえていません(oboeteimasen): tôi không nhớ\r\nあみだな(amidana)：giá lưới, giá hành lý\r\nたしか(tashika)：nếu không lầm thì\r\nよつや(yotsuya)：tên một nhà ga ở tokyo\r\nじしん(jishin)：động đất\r\nかべ(kabe)：bức tường\r\nはり(hari)：kim đồng hồ\r\nさします(sashimasu)：chỉ\r\nえきまえ(ekimae)：khu vực trước ga\r\nたおれます(taoremasu)：đổ\r\nにし(nishi)：phía tây\r\nほう(hou)：hướng, phương hướng\r\nさんのみや(sannomiya)：tên một địa điểm ở kobe', 'n4', 29);
INSERT INTO `hoctiengnhat`.`volca` (`content`, `course`, `lesson`) VALUES ('はります(harimasu)：dán\r\nかけます(kakemasu)：treo\r\nかざります(kazarimasu)：trang trí\r\nならべます(narabemasu)：xếp thành hàng\r\nうえます(uemasu)：trồng (cây)\r\nもどします(modoshimasu)：đưa về, trả về\r\nまとめます(matomemasu)：nhóm lại, tóm tắt\r\nかたづけます(katadukemasu)：dọn dẹp, sắp xếp\r\nしまいます(shimaimasu)：cất vào, để vào\r\nきめます(kimemasu)：quyết định\r\nしらせます(shirasemasu)：thông báo\r\nそうだんします(soudanshimasu)：thảo luận, trao đổi , bàn bạc\r\nよしゅうします(yoshuushimasu)：chuẩn bị bài mới\r\nふくしゅうします(fukushuushimasu)： ôn bài cũ\r\nそのままにします(sonomamanishimasu)：để nguyên như thế\r\nおこさん(okosan)：con (dùng đối với người khác)\r\nじゅぎょう(jyugyou)：giờ học\r\nこうぎ(kougi)： bài giảng\r\nミーテイング(miiteingu)： cuộc họp\r\nよてい(yotei)：kế hoạch, dự định\r\nおしらせ(oshirase)：bản thông báo\r\nあんないしょ(annaisho)：tài liệu hướng dẫn\r\nカレンダー(karendaa)：lịch , tờ lịch\r\nポスター(posutaa)：tờ quảng cáo, tờ áp phích\r\nごみばこ(gomibako)：thùng rác\r\nにんぎょう(ningyou)： con búp bê, con rối\r\nかびん(kabin)：lọ hoa\r\nかがみ(kagami)：cái gương\r\nひきだし(hikidashi)：ngăn kéo\r\nげんかん(genkan)：cửa vào\r\nろうか(rouka)：hành lang\r\nかべ(kabe)：bức tường\r\nいけ(ike)：cái ao\r\nこうばん(kouban)： trạm cảnh sát\r\nもとのところ(motonotokoro)：địa điểm ban đầu\r\nまわり(mawari)：xung quanh\r\nまんなか(mannaka)：giữa, trung tâm\r\nすみ(sumi)：góc\r\nまだ(mada)：chưa\r\n―ほど(–hodo)：chừng—\r\nよていひょう(yoteihyou)：thời khóa biểu\r\nごくろうさま(gokurousama)：anh, chị đã làm việc vất vả/cảm ơn anh, chị\r\nきぼう(kibou)：hi vọng, nguyện vọng\r\nなにかごきぼうがありますか(nanikagokibougaarimasuka)：anh/chị có nguyện vọng gì không?\r\nミュージカル(myuujikaru)：ca kịch\r\nそれはいいですな(sorehaiidesuna)： điều đó được đấy nhỉ\r\nまるい(marui)：tròn\r\nつき(tsuki)：mặt trăng\r\nちきゅう(chikyuu)：trái đất\r\nうれしい(ureshii)：vui\r\nいや(iya)：chán, ghét, không chấp nhận được\r\nすると(suruto)： sau đó, tiếp đó\r\nめがさめます(megasamemasu)：tỉnh giấc, mở mắt', 'n4', 30);
INSERT INTO `hoctiengnhat`.`volca` (`content`, `course`, `lesson`) VALUES ('はじまります(hajimarimasu)：bắt đầu\r\nつづけます(tsudukemasu)：tiếp tục\r\nみつけます(mitsukemasu)：tìm thấy\r\nうけます(ukemasu)：thi(kỳ thi)\r\nにゅうがくします(nyuugakushimasu)：nhập học,vào\r\nそつぎょうします(sotsugyoushimasu)：tốt nghiệp\r\nしゅっせきします(shussekishimasu)：tham gia, tham dự\r\nきょうけいします(kyoukeishimasu)：nghỉ giải lao\r\nれんきゅう(renkyuu)：ngày nghỉ liền nhau\r\nさくぶん(sakubun)：bài văn\r\nてんらんかい(tenrankai)：triển lãm\r\nけっこんしき(kekkonshiki)：lễ cưới, đám cưới\r\nそうしき(soushiki)：lễ tang, đám tang\r\nしき(shiki)：lễ , đám\r\nほんしゃ(honsha)：trụ sở chính\r\nしてん(shiten)：chi nhánh\r\nきょうかい(kyoukai)：nhà thờ\r\nだいがくいん(daigakuin)：cao học, sở giáo dục trên đại học\r\nどうぶつえん(doubutsuen)：vườn thú, vườn bách thú\r\nおんせん(onsen)：suối nước nóng\r\nおきゃくさん(okyakusan)：khách hàng\r\nだれか(dareka)：ai đó\r\nずっと(zutto)： suốt liền\r\nピカソ(pikaso)： pablo picasso , danh họa người tây ban nha\r\nうえのこうえん(uenokouen)：công viên ueno\r\nのこります(nokorimasu)：ở lại\r\nつきに(tsukini)：một tháng\r\nふつうの(futsuuno)：thường, thông thường\r\nインターネット(intaanetto)：internet\r\nむら(mura)：làng\r\nえいがかん(eigakan)：rạp chiếu phim\r\nいや(iya)：chán , ghét\r\nとじます(tojimasu)：đóng , nhắm\r\nとかい(tokai)：thành phố\r\nせかいじゅう(sekaijyuu)：khắp thế giới\r\nあつまります(atsumarimasu)：tập trung\r\nうつくしい(utsukushii)：đẹp\r\nしぜん(shizen)：tự nhiên, thiên nhiên\r\nすばらしさ(subarashii)：tuyệt vời\r\nきがつきます(kigatsukimasu)：để ý, nhận ra', 'n4', 31);
INSERT INTO `hoctiengnhat`.`volca` (`content`, `course`, `lesson`) VALUES ('うんどうします(undoushimasu)：vận động, tập thể thao\r\nせいこうします(seikoushimasu)：thành công\r\nしっぱいします(shippaishimasu)：thất bại, trượt\r\nごうかくします(goukakushimasu)：đỗ\r\nもどります(modorimasu)：quay lại, trở lại\r\nやみます(yamimasu)：tạnh ,ngừng\r\nはれます(haremasu)：nắng, quang đãng\r\nくもります(kumorimasu)：có mây, mây mù\r\nふきます(fukimasu)：thổi\r\nなおります(naorimasu)：khỏi\r\nつづきます(tsudukimasu)：tiếp tục, tiếp diễn\r\nひきます(hikimasu)：bị (cảm)\r\nひやします(hiyashimasu)：làm lạnh\r\nしんぱい(shinpai)：lo lắng\r\nじゅうぶん(jyuubun)： đủ\r\nおかしい(okashii)： có vấn đề, không bình thường\r\nうるさい(urusai)：ồn ào\r\nやけど(yakedo)：bỏng\r\nけが(kega)：bị thương\r\nせき(seki)：ho\r\nインフルエンザ(infuruenza)： cúm dịch\r\nそら(sora)：bầu trời\r\nたいよう(taiyou)： mặt trời\r\nほし(hoshi)：sao , ngôi sao\r\nつき(tsuki)：mặt trăng\r\nかぜ(kaze)：gió\r\nきた(kita)：phía bắc\r\nみなみ(minami)：phía nam\r\nにし(nishi)： phía tây\r\nひがし(higashi)：phía đông\r\nすいどう(suidou)：nước máy\r\nエンジン(enjin)： động cơ\r\nチーム(chiimu)：đội\r\nこんや(konya)：tối nay, đêm nay\r\nゆうがた(yuugata)：chiều tối\r\nまえ(mae)：trước\r\nおそく(osoku)：muộn , khuya\r\nこんなに(konnani)：như thế này\r\nそんなに(sonnani)：như thế đó\r\nあんなに(annani)：như thế kia\r\nもしかしたら(moshikashitara)：có thể, biết dâu là, có khả năng\r\nそれはいけませんね(sorehaikemasenne)：thế thì thật không tốt.\r\nオリンピック(orimpikku)：olympic', 'n4', 32);
INSERT INTO `hoctiengnhat`.`volca` (`content`, `course`, `lesson`) VALUES ('にげます(nigemasu)： chạy trốn, bỏ chạy\r\nさわぎます(sawagimasu)：làm ồn, làm rùm beng\r\nあきらめます(akiramemasu)：từ bỏ, đầu hàng\r\nなげます(nagemasu)：ném\r\nまもります(mamorimasu)：bảo vệ, tuân thủ\r\nあげます(agemasu)：nâng lên, tăng lên\r\nさげます(sagemasu)：hạ xuống, giảm xuống\r\nつたえます(tsutaemasu)：truyền đạt\r\nちゅういします(chuuishimasu)：chú ý\r\nはずします(hazushimasu)：rời, không có ở (chỗ ngồi)\r\nだめ(dame)：không được, không thể\r\nせき(seki)：chỗ ngồi, ghế\r\nファイト(faito)：’quyết chiến’, ‘cố lên’\r\nマーク(maaku)：kí hiệu\r\nボール(bouru)：quả bóng\r\nせんたくき(sentakuki)： máy giặt\r\n―き(–ki)：máy–\r\nきそく(kisoku)：quy tắc, kỷ luật\r\nしようきんし(shiyoukinshi)：cấm sử dụng\r\nたちいりきんし(tachiirikinshi)：cấm vào\r\nいりぐち(iriguchi)：cửa vào\r\nでぐち(deguchi)：cửa ra\r\nひじょうぐち(hijyouguchi)：cửa thoát hiểm\r\nむりょう(muryou)：miễn phí\r\nほんじつきゅうぎょう(honjitsukyuugyou)：hôm nay đóng cửa, hôm nay nghỉ\r\nえいぎょうちゅう(eigyouchuu)： đang mở cửa\r\nしようちゅう(shiyouchuu)：đang sử dụng\r\n―ちゅう(–chuu)： đang–\r\nどういうー(douiu–)： –gì, — thế nào\r\nもう(mou)：không—nữa', 'n4', 33);
INSERT INTO `hoctiengnhat`.`volca` (`content`, `course`, `lesson`) VALUES ('みがきます(migakimasu)：mài, đánh (răng)\r\nくみたてます(kumitatemasu)：lắp ráp, lắp đặt\r\nおります(orimasu)：gấp, gập, bẻ gãy\r\nきがつきます(kigatsukimasu)：nhận thấy, phát hiện\r\nつけます(tsukemasu)：chấm\r\nみつかります(mitsukarimasu)：được tìm thấy\r\nします(shimasu)：đeo , thắt\r\nしつもんします(shitsumonshimasu)： hỏi\r\nほそい(hosoi)：gầy, hẹp, thon\r\nふとい(futoi)：béo, to\r\nぼんおどり(bonodori)： múa trong hội bon\r\nスポーツクラブ(supoutsukurabu)：câu lạc bộ thể thao\r\nかぐ(kagu)：gia cụ , đồ dùng nội thất\r\nキー(kii)：chìa khóa\r\nシートべルト(shiitoberuto)： dây an toàn\r\nせつめいしょ(setsumeisho)：quyển hướng dẫn\r\nず(zu)：sơ đồ, hình vẽ\r\nせん(sen)：đường\r\nやじるし(yajirushi)：dấu mũi tên\r\nくろ(kuro)：màu đen\r\nしろ(shiro)：màu trắng\r\nあか(aka)：màu đỏ\r\nあお(ao)：màu xanh da trời\r\nこん(kon)：màu xanh lam\r\nきいろ(kiiro)：màu vàng\r\nちゃいろ(chairo)：màu nâu\r\nしょうゆ(shouyu)：xì- dầu\r\nソース(sousu)：nước chấm, nước xốt\r\nゆうべ(yuube)；tối qua, đêm qua\r\nさっき(sakki)：vừa rồi, vừa lúc nãy', 'n4', '34');
INSERT INTO `hoctiengnhat`.`volca` (`content`, `course`, `lesson`) VALUES ('さきます(sakimasu)：nở (hoa)\r\nかわります(kawarimasu)：thay đổi, đổi\r\nこまります(komarimasu)：rắc rối, khó xử, có vấn đề\r\nつけます(tsukemasu)：vẽ , đánh dấu\r\nひろいます(hiroimasu)：nhặt lên\r\nかかります(kakarimasu)：có điện thoại\r\nらく(raku)：thoải mái, nhàn hạ, dễ dàng\r\nただしい(tadashii)：đúng, chính xác\r\nめずらしい(mezurashii)：hiếm có\r\nかた(kata)：vị, người\r\nむこう(mukou)：bên kia, bên đấy\r\nしま(shima)：đảo, hòn đảo\r\nむら(mura)：làng\r\nみなと(minato)：cảng, bến cảng\r\nきんじょ(kinjo)：hàng xóm, khu vực lân cận\r\nおくじょう(okujou)：mái nhà, nóc nhà\r\nかいがい(kaigai)：nước ngoài, hải ngoại\r\nやまのぼり(yamanobori)：leo núi\r\nハイキング(haikinggu)：đi bộ trên núi\r\nきかい(kikai)：cơ hội\r\nきょか(kyoka)：phép, giấy phép\r\nまる(maru)：tròn, vòng tròn\r\nそうさ(sousa)：thao tác\r\nほうほう(houhou)：phương pháp\r\nせつび(setsubi)：thiết bị\r\nカ－テン(kaaten)：cái rèm\r\nひも(himo)：sợi dây\r\nふた(futa)：cái nắp\r\nは(ha)：cái lá\r\nきょく(kyoku)：bài hát, bản nhạc\r\nたのしみ(tanoshimi)：niềm vui, điều vui\r\nもっと(motto)：hơn , nữa\r\nはじめに(hajimeni)：đầu tiên , trước hết\r\nこれでおわります(koredeowarimasu)：đến đây là hết, chúng ta dừng ở đây', 'n4', 35);
INSERT INTO `hoctiengnhat`.`volca` (`content`, `course`, `lesson`) VALUES ('とどきます(todokimasu)：được gửi đến, được chuyển đến\r\nでます(demasu)：tham gia, tham dự\r\nうちます(uchimasu)： đánh\r\nちょきんします(chokinshimasu)： tiết kiệm tiền, để dành tiền\r\nふとります(futorimasu)：béo lên, tăng cân\r\nやせます(yasemasu)： gầy đi, giảm cân\r\nすぎます(sugimasu)：quá\r\nなれます(naremasu)：làm quen, quen\r\nかたい(katai)：cứng\r\nやわらかい(yawarakai)： mềm\r\nでんしー(denshi–)：– điện tử\r\nけいたいー(keitai–)：– cầm tay\r\nこうじょう(koujou)：nhà máy, phân xưởng\r\nけんこう(kenkou)： sức khỏe\r\nけんどう(kendou)：kiếm đạo\r\nまいしゅう(maishuu)： hàng tuần\r\nまいつき(maitsuki)： hàng tháng\r\nまいとし(maitoshi)： hàng năm\r\nやっと(yatto)：cuối cùng thì\r\nかなり(kanari)：khá, tương đối\r\nかならず(kanarazu)：nhất định\r\nぜったいに(zettaini)：tuyệt đối\r\nじょうずに(jouzuni)：giỏi, khéo\r\nできるだけ(dekirudake)：cố gắng\r\nこのごろ(konogoro)：gần đây, dạo này\r\nそのほうがー(sonohouga)：cái đó–hơn', 'n4', 36);
INSERT INTO `hoctiengnhat`.`volca` (`content`, `course`, `lesson`) VALUES ('ほめます(homemasu)：khen\r\nしかります(shikarimasu)：mắng\r\nさそいます(sasoimasu)：mời, rủ rê\r\nおこします(okoshimasu)：đánh thức\r\nしょうたいします(shoutaishimasu)：mời\r\nたのみます(tanomimasu)：nhờ\r\nちゅういします(chuuishimasu)：chú ý, nhắc nhở\r\nとります(torimasu)：ăn trộm , lấy cắp\r\nふみます(fumimasu)：giẫm , giẫm lên\r\nこわします(kowashimasu)：phá, làm hỏng\r\nよごします(yogoshimasu)：làm bẩn\r\nおこないます(okonaimasu)：thực hiện, tiến hành\r\nゆしゅつします(yushutsushimasu)：xuất khẩu\r\nゆにゅうします(yunyuushimasu)：nhập khẩu\r\nほんやくします(honyakushimasu)：dịch (sách, tài liệu)\r\nはつめいします(hatsumeishimasu)：phát minh\r\nはっけんします(hakkenshimasu)：phát kiến, tìm ra\r\nせっけいします(sekkeishimasu)：thiết kế\r\nこめ(kome)：gạo\r\nむぎ(mugi)：lúa mạch\r\nせきゆ(sekiyu)： dầu mỏ\r\nげんりょう(genryou)： nguyên liệu\r\nデート(deeto)：cuộc hẹn hò\r\nどろぼう(dorobou)：kẻ trộm\r\nけいかん(keikan)：cảnh sát\r\nけんちくか(kenchikuka)：kiến trúc sư\r\nかがくしゃ(kagakusha)：nhà khoa học\r\nまんが(mangga)：truyện tranh\r\nせかいじゅう(sekaijuu)：khắp thế giới, toàn thế giới\r\n―じゅう(–juu)：khắp–\r\n―によって(–ni yotte)：do–\r\nよかったですね(yokattadesune)：may nhỉ\r\nうめたてます(umetatemasu)：lấp\r\nぎじゅつ(gijutsu)：kỷ thuật\r\nとち(tochi)：đất, diện tích đất\r\nそうおん(souon)：tiếng ồn\r\nりようします(riyoushimasu)：sử dụng\r\nアクセス(akusesu)：nối, giao thông đi đến', 'n4', 37);
INSERT INTO `hoctiengnhat`.`volca` (`content`, `course`, `lesson`) VALUES ('そだてます(sodatemasu)：nuôi , trồng\r\nはこびます(hakobimasu)：chở, vận chuyển\r\nなくなります(nakunarimasu)：mất, qua đời\r\nにゅういんします(nyuuinshimasu)：nhập viện\r\nたいいんします(taiinshimasu)：xuất viện\r\nいれます(iremasu)：bật\r\nきります(kirimasu)：tắt\r\nかけます(kakemasu)：khóa\r\nきもちがいい(kimochigaii)： dễ chịu, thư giản\r\nきもちがわるい(kimochigawarui)：khó chịu\r\nおおきなー(ookina–)：–to, –lớn\r\nちいさなー(chiisana–)：–nhỏ, –bé\r\nあかちゃん(akachan)：em bé\r\nしょうがっこう(shougakkou)： trường tiểu học\r\nちゅうがっこう(chuugakkou)：trường trung học cơ sở\r\nえきまえ(ekimae)：khu vực trước nha ga\r\nかいがん(kaigan)：bờ biển\r\nうそ(uso)：nói dối, lời nói dối\r\nしょるい(shorui)：giấy tờ,tài liệu\r\nでんげん(dengen)：nguồn điện , công tắc điện\r\n―せい(–sei)： sản xuất tai–\r\nあ、いけない(a,ikenai)：Ôi, hỏng mất rồi./Ôi, trời ơi\r\nおさきに(osakini)：Tôi xin phép về trước\r\nかいらん(kairan)：Tập thông báo\r\nけんきゅうしつ(kenkyuushitsu)： Phòng nghiên cứu\r\nきちんと(kichinto)： Nghiêm chỉnh, hẳn hoi, đứng đắn\r\nせいりします(seirishimasu)： sắp xếp\r\nはんこ(hanko)：con dấu, dấu\r\nおします(oshimasu)：đóng\r\nふたご(futago)：cặp sinh đôi\r\nしまい(shimai)：chị em\r\n５ねんせい(5 nensei) ： học sinh năm thứ 5\r\nにています(niteimasu)：giống\r\nせいかく(seikaku)：tính cách\r\nおとなしい(otonashii)：hiền lành, trầm\r\nせわをします(sewaoshimasu)：chăm sóc , giúp đỡ\r\nじかんがたちます(jikangatachimasu)： thời gian trôi đi\r\nだいすき(daisuki)：rất thích\r\nクラス(kurasu)：lớp học, lớp\r\nけんかします(kenkashimasu)： cãi nhau\r\nふしぎ（fushigi）：bí ẩn , kỳ thú, khó hiểu.', 'n4', 38);
INSERT INTO `hoctiengnhat`.`volca` (`content`, `course`, `lesson`) VALUES ('こたえます(kotaemasu)：trả lời\r\nたおれます(taoremasu)：đổ\r\nやけます(yakemasu)：cháy\r\nとおります(toorimasu)：đi qua\r\nしにます(shinimasu)：chết\r\nびっくりします(bikkurishimasu)：ngạc nhiên, giật mình\r\nがっかりします(gakkarishimasu)：thất vọng\r\nあんしんします(anshinshimasu)：yên tâm\r\nちこくします(chikokushimasu)：đến chậm , đến muộn\r\nそうたいします(soutaishimasu)：về sớm, ra sớm\r\nけんかします(kenkashimasu)：cãi nhau\r\nりこんします(rikonshimasu)：ly dị, ly hôn\r\nふくざつ(fukuzatsu)：phức tạp\r\nじゃま(jama)：cản trở, chiếm diện tích\r\nきたない(kitanai)：bẩn\r\nうれしい(ureshii)：vui mừng\r\nかなしい(kanashii)：buồn, đau thương\r\nはずかしい(hazukashii)：xấu hổ, thẹn, hổ thẹn\r\nじしん(jishin)：động đất\r\nたいふう(taifuu)：bão\r\nかじ(kaji)：hỏa hoạn\r\nじこ(jiko)：tai nạn, sự cố\r\nみあい(miai)：nam nữ làm quen qua giới thiệu, làm mối\r\nでんわだい(denwadai)：tiền điện thoại, phí điện thoại\r\nフロント(furonto)：bộ phận tiếp tân\r\n―ごうしつ(–goushitsu)：phòng số–\r\nあせ(ase)：mồ hôi\r\nタオル(taoru)：khăn lau, khăn tắm\r\nせっけん(sekken)：xà phòng\r\nおおぜい(oozei)：nhiều người\r\nおつかれさまでした(otsukaresamadeshita)：chắc anh/chị đã mệt vì làm việc\r\nうかがいます(ukagaimasu)：tôi đến thăm\r\nとちゅうで(tochuude)：giữa đường, giữa chừng\r\nトラック(torakku)：xe tải\r\nぶつかります(butsukarimasu)：đâm , va chạm\r\nならびます(narabimasu)：xếp hàng\r\nおとな(otona)：người lớn\r\nようふく(youfuku)：quần áo kiểu tây âu\r\nせいようかします(seiyoukashimasu)：tây âu hóa\r\nあいます(aimasu)：vừa, hợp\r\nいまでは(imadeha)：bây giờ(thì)\r\nせいじんしき(seijinshiki)：lễ trưởng thành, lễ thành nhân.', 'n4', 39);
INSERT INTO `hoctiengnhat`.`volca` (`content`, `course`, `lesson`) VALUES ('かぞえます(kazoemasu)：đếm\r\nはかります(hakarimasu)：đo, cân\r\nたしかめます(tashikamemasu)：xác nhận\r\nあいます(aimasu)：vừa , hợp\r\nしゅっぱつします(shuppatsushimasu)：xuất phát, khởi hành\r\nとうちゃくします(touchakushimasu)：đến , đến nơi\r\nよいます(yoimasu)：say\r\nきけん(kiken)：nguy hiểm\r\nひつよう(hitsuyou)：cần thiết\r\nうちゅう(uchuu)：vũ trụ\r\nちきゅう(chikyuu)：trái đất\r\nぼうねんかい(bounenkai)：tiệc tất niên\r\nしんねんかい(shinnenkai)：tiệc tân niên\r\nにじかい(nijikai)：bữa tiệc thứ hai, tăng hai\r\nたいかい(taikai)：đại hội , cuộc thi\r\nマラソン(marason)：ma-ra-tong\r\nコンテスト(kontesuto)：cuộc thi\r\nおもて(omote)：phía trước , mặt trước\r\nうら(ura)：phía sau , mặt sau\r\nへんじ(henji)：hồi âm , trả lời\r\nもうしこみ(moushikomi)：đăng ký\r\nほんとう(hontou)：thật\r\nまちがい(machigai)：sai , lỗi\r\nきず(kizu)：viết thương\r\nズボン(zubon)：cái quần\r\nながさ(nagasa)：chiều dài\r\nおもさ(omosa)：cân nặng, trọng lượng\r\nたかさ(takasa)：chiều cao\r\nおおきさ(ookisa)：cỡ , kích thước\r\n―びん(–bin)：chuyến bay–\r\n―ごう(–gou)：số–\r\n―こ(–ko)：cái, cục , viên\r\n―ほん(–hon)：cái(đơn vị đếm vật dài)\r\n―はい(–hai)：–chén, –cốc\r\n―キロ(–kiro)：–ki-lo, –cân\r\n―グラム(–guramu)：–gam\r\nーセンチ(–senchi)：–xăng-ti-mét\r\nーミリ(–miri)：–mi-li-mét\r\n―いじょう(–ijou)： trở lên, trên\r\n―いか(–ika)：trở xuống, dưới\r\nさあ(saa)：à.., ồ..,(dùng khi không rõ về điều gì đó)', 'n4', 40);

CREATE TABLE `kanji` (
	`ID` INT NOT NULL AUTO_INCREMENT,
	`CHAR` VARCHAR(50) NULL DEFAULT '0' COLLATE 'utf8_general_ci',
	`AMHV` VARCHAR(50) NULL DEFAULT '0' COLLATE 'utf8_general_ci',
	`NGHIA` VARCHAR(50) NULL DEFAULT '0' COLLATE 'utf8_general_ci',
	`ON` VARCHAR(50) NULL DEFAULT '0' COLLATE 'utf8_general_ci',
	`KUN` VARCHAR(50) NULL DEFAULT '0' COLLATE 'utf8_general_ci',
	PRIMARY KEY (`ID`)
)
COLLATE='latin1_swedish_ci'
ENGINE=InnoDB
;
ALTER TABLE `kanji`
	ALTER `CHAR` DROP DEFAULT,
	ALTER `AMHV` DROP DEFAULT,
	ALTER `NGHIA` DROP DEFAULT;
ALTER TABLE `kanji`
	CHANGE COLUMN `CHAR` `CHAR` VARCHAR(50) NULL COLLATE 'utf8_general_ci' AFTER `ID`,
	CHANGE COLUMN `AMHV` `AMHV` VARCHAR(50) NULL COLLATE 'utf8_general_ci' AFTER `CHAR`,
	CHANGE COLUMN `NGHIA` `NGHIA` VARCHAR(50) NULL COLLATE 'utf8_general_ci' AFTER `AMHV`;

