INSERT INTO `hoctiengnhat`.`gram` (`content`, `course`, `lesson`) VALUES ('I/Cấu trúc + Ngữ pháp: 1.Cấu trúc: V(て形） Vないーー＞なくて,～～～ Aいーー＞くて、　　～～～ Aなーー＞で、　　　～～～ Nーー＞で、　　　　～～～\r\n2.Ngữ pháp:\r\nMẫu câu dùng để nếu lý do, nguyên nhân nhằm biểu lộ cảm xúc, cảm tưởng.Các từ chỉ cảm xúc thường được sử dụng như là:\r\nびっくりする–>Ngạc nhiên\r\nがっかりする–> Thất vọng\r\n安心する–> Yên tâm\r\n困る –> Khó khăn\r\nうれしい—>vui sướng\r\nかなしい–>buồn bã\r\n残念—-> Đáng tiếc\r\n。。。\r\nVí dụ:\r\n-手紙を読んで、びっくりしました\r\nてがみをよんで、びっくりしました。\r\nTôi ngạc nhiên sau khi đọc thư\r\n-電話をもらって、安心しました\r\nでんわをもらって、あんしんしました\r\nNhận được điện thoai thì tôi thấy an tâm\r\n-彼女は連絡がなくて、心配です\r\nかなじょはれんらくがなくて、しんぱいです\r\nCô ta không có liên lạc gì nên tôi thấy lo lắng\r\n-事故で人が大勢死にました\r\nじこでひとがおおぜいしにました\r\nVì tai nạn nên đã có nhiều người chết.\r\nII/Cấu trúc + Ngữ pháp:\r\n1.Cấu trúc:\r\nV　普通形　　+ ので、～～\r\nAい普通形　　+ ので、～～\r\nAな普通形 + ので、～～\r\nN　普通形 + ので、～～\r\n2.Ngữ pháp:\r\nĐộng từ , tính từ, danh từ ở thể thông thường + ので dùng để chỉ lý do, nguyên nhân một cách khách quan theo diễn biến, tình huống tự nhiên. Nghĩa tương đương với から、 nhưng mang âm điệu nhẹ nhàng hơn nên thường được dùng trong các tình huống xin phép.\r\n3.Ví dụ:\r\n用事があるので、お先に失礼します\r\nようじがあるので、おさきにしつれいします\r\nVì tôi có việc bận, nên tôi xin phép về trước\r\n調子が悪いので、早退していただけませんか\r\nちょうしがわるいので、そうたいしていただけませんか\r\nVì sức khỏe của tôi không được tốt nên cho tôi về sớm có được không?\r\n日本語が分からないので、ベトナム語で話していただけませんか\r\nにほんごがわからないので、べとなむごではなしていただけませんか\r\nVì tôi không hiểu tiếng Nhật nên làm ơn nói chuỵên bằng tiếng Việt có được không?\r\n昨日は誕生日だったので、ビールを飲みすぎました\r\nきのうはたんじょうびだったので、ビールをのみすぎました\r\nVì hôm qua là sinh nhật nên tôi đã uống quá nhiều bia.', 'n4', 39);
INSERT INTO `hoctiengnhat`.`gram` (`content`, `course`, `lesson`) VALUES ('I/Cấu trúc + Ngữ pháp 疑問詞(Từ để hỏi) + V(普通形-Thể thông thường) + か、～～～～ Không biết là có như thế nào đó hay không? -Ví dụ; -会議はいつ終わるか、わかりません Tôi không biết là khi nào thì cuộc họp kết thúc.\r\n-ビールが何本あるか、教えてください\r\nHãy nói cho tôi biết là có bao nhiêu lon bia.\r\n-箱の中に何があるか、調べてください\r\nHãy tìm hiểu xem trong hộp có gì hay không?\r\nII/Cấu trúc + Ngữ pháp\r\nKhi không có từ để hỏi thì cấu trúc là :V(普通形-Thể thông thường) + かどうか、～～～～\r\n-Ví dụ;\r\n-傷がないかどうか、調べてください\r\nHãy kiểm tra xem là có bị thương hay không?\r\n-荷物が着いたかどうか、確かめてください\r\nHãy xác nhận lại xem là hàng hóa đã đến hay chưa?\r\n-その話はほんとうかどうか、わかりません\r\nKhông biết là câu chuyện đấy có thật hay không?\r\nIII/Cấu trúc + Ngữ pháp\r\nCấu trúc; Vて（ Động từ thể て） + みます\r\nNgữ pháp; Thử làm một hành động V\r\nVí dụ;\r\n-新しい靴を履いてみます\r\nあたらしいくつをはいてみます。\r\nĐi thử đôi giày mới\r\n-もう一度がんばってみます\r\nThử cố gắng thêm một lần nữa\r\n-日本語で説明してみます\r\nThử giải thích bằng tiếng Nhật', 'n4', 40);
INSERT INTO `hoctiengnhat`.`gram` (`content`, `course`, `lesson`) VALUES (' Cách diễn đạt cho nhận\r\n \r\nTrong bài 7 và 24, ta đã học cách diễn đạt cho và nhận đồ vật và hành động. Trong bài này, ta sẽ học cách diễn đạt cho và nhận đồ vật và hành động khác, có phản ánh quan hệ giữa người cho và người nhận。\r\n \r\n1) N1に N2を やります\r\n \r\nKhi người nhận là tiểu bối, động vật hoặc cây cỏ, người ta thường dùng やります. Tuy nhiên, nếu là người, hiện nay ở Nhật, người ta thường dùng あげます hơn.\r\n \r\n① わたしは 息子に お菓子を やりました（あげました）。\r\n \r\ntôi cho con trai kẹo\r\n \r\n② わたしは 犬に えさを やりました。\r\n \r\ntôi cho chó ăn\r\n \r\nLưu ý: Khi người nói muốn biểu thị sự tôn kính đặc biệt, sẽ dùng さしあげます」。\r\n \r\n2) N1に N2を いただきます\r\n \r\nKhi người nói thu nhận từ bậc trưởng bối, không dùng もらいます mà dùng いただきます.\r\n \r\n③ わたしは 部長に お土産を いただきました。\r\n \r\nTôi được trưởng phòng tặng quà kỷ niệm.\r\n \r\n3) ［わたしに］Nを くださいます\r\n \r\nKhi trưởng bối cho người nói cái gì đó, không dùng くれま mà dùng すくださいます.\r\n \r\n④ 部長が わたしに お土産を くださいました。\r\n \r\ntrưởng phòng tặng quà kỷ niệm cho tôi.\r\n \r\nCũng dùng くださいます khi người nhận là thành viên trong gia đình người nói.\r\n \r\n⑤ 部長が 娘に お土産を くださいました。\r\n \r\ntrưởng phòng tặng quà kỷ niệm cho con gái tôi.\r\n \r\nCho và nhận hành động\r\n \r\nKhi cho và nhận hành động, phải dùng 「やります」「いただきます」「くださいます」。\r\n \r\nXem ví dụ dưới đây:\r\n \r\n1) Vて やります\r\n⑥わたしは 息子に 紙飛行機を 作って やりました（あげました）。\r\nTôi làm máy bay giấy cho con trai.\r\n⑦わたしは 犬を 散歩に 連れて 行って やりました。\r\ntôi dẫn chó đi dạo\r\n⑧わたしは 娘の 宿題を 見て やりました（あげました）。\r\ntôi kiểm tra bài tập cho con gái\r\nLưu ý:\r\nGiống hình thức ～て あげます đã học trong bài 24, hình thức ～て あげます／さしあげ\r\nます mang lại cảm giác kiêu ngạo, vì thế tốt nhất không dùng cách này khi nói chuyện trực tiếp với trưởng bối.\r\n2) Vて いただきます\r\n⑨わたしは 課長に 手紙の まちがいを 直して いただきました。\r\ntôi được trưởng phòng sửa lỗi sai trong thư。\r\n3) Vて くださいます\r\n⑩部長の 奥さんは［わたしに］お茶を 教えて くださいました。\r\nVợ trưởng phòng dạy trà đạo cho tôi\r\n⑪部長は［わたしを］駅まで 送って くださいま/* large SQL query (2.7 KiB), snipped at 2,000 characters */
INSERT INTO `hoctiengnhat`.`gram` (`content`, `course`, `lesson`) VALUES ('１ . Chỉ mục đích.\r\nVru }ために、～ để\r\nN }ために、～ vì\r\n自分の 店を 持つ ために、貯金して います。\r\nTôi đang để dành tiền để có cửa hàng cho riêng mình.\r\n引っ越しの ために、車を 借ります。\r\nTôi mượn xe để chuyển nhà.\r\n健康の ために、毎朝 走って います。\r\nMỗi sáng tôi chạy bộ để khoẻ mạnh.\r\n家族の ために、うちを 建てます\r\nXây nhà vì gia đình.\r\n“Nの ために”cũng được dùng để biểu thị ý“vì lợi ích của”, như trong câu （4）。\r\n \r\nChú ý 1: Chữ ～ように đã học trong bài 36 cũng được dùng để chỉ mục đích.\r\n～ように dùng với động từ không ý chí, còn ～ために được dùng với động từ biểu lộ ý chí. Hãy so sánh hai câu dưới đây:\r\n①自分の 店を 持っ ために、貯金して います。\r\ntôi đang để dành tiền để có cửa hàng riêng.\r\n⑤自分の 店が 持てるように、貯金して います。\r\ntôi đang để dành tiền để có thể có cửa hàng riêng.\r\n \r\n① có nghĩa là người này đặt mục tiêu “có cửa hàng riêng” và “đang để dành tiền” đạt đạt mục tiêu đó. Còn ⑤ có nghĩa là mục tiêu của người này là tình trạng “có thể có cửa hàng” và người đó “đang để dành tiền” để tiến đến tình trạng này.\r\n \r\nChú ý 2「なります」như dưới đây có thể biểu thị ý chí ⑥、hoặc ko ý chí ⑦。\r\n⑥弁護士に なる ために、法律を 勉強して います。\r\ntôi đang học luật để trở thành luật sư.\r\n⑦日本語が 上手に なるように、毎日 勉強しています。\r\nmỗi ngày tôi học hành để trở nên giỏi tiếng Nhật.\r\n２ . Vru の}に ～\r\nN の}に ～\r\nNhư đã học ở bài 38, có thể dùng Vruの như một cụm danh từ.\r\nVruのに và N に theo sau các từ つかう, いい, べんりだ, やくに たつ, ［じかんが］かかる v.v… để chỉ mục đích.\r\n⑧この はさみは 花を 切るのに 使います。\r\nKéo này dùng để cắt hoa。\r\n⑨この かばんは 大きくて、旅行に 便利です。\r\nBao này lớn nên tiện cho việc đi du lịch.\r\n⑩電話番号を 調べるのに 時間が かかりました。\r\nMất nhiều thời gian để tìm số điện thoại.\r\nChú ý Có nhiều cách diễn tả mục đích. Chúng ta hãy tổng kết các cách chỉ mục đích đã học từ trước đến nay.\r\nVます に ( 行きます／来ます／帰ります) （Bài13）\r\nN に\r\n⑪神戸へ 船を 見に 行きます。\r\nĐi Kobe xem thuyền\r\n⑫日本へ 経済の 勉強に 来ました。\r\nĐến Nhật học kinh tế.\r\nVru } (biểu hiện ko ý chí)ように、～(biểu thị ý chí)（36）\r\nVない\r\n⑬早く 届くように、速達で 出します。\r\nTôi sẽ/* large SQL query (2.8 KiB), snipped at 2,000 characters */
INSERT INTO `hoctiengnhat`.`gram` (`content`, `course`, `lesson`) VALUES ('１. Có vẻ\r\n \r\nVます\r\n \r\nAい（ ～い／）} そうです\r\n \r\nAな［な／］\r\n \r\nDùng mẫu này khi bề ngoài của một vật/người/cảnh gợi cho bạn một giả thiết, suy đoán.\r\n \r\n1) Vますそうです\r\n \r\nDùng mẫu này khi trạng thái hiện tại khiến người nói đoán sắp có chuyện xảy ra.\r\n \r\nいまにも, もうすぐ, これから v.v… được thêm vào để chỉ thời gian xảy ra chuyện đó.\r\n \r\n今にも 雨が 降りそうです。\r\n \r\nCó lẽ sắp mưa nhỉ。\r\n \r\nシャンプーが なくなりそうです。\r\n \r\nxà-bông sắp hết rồi.\r\n \r\nもうすぐ 桜が 咲きそうです。\r\n \r\nhoa có lẽ sắp nở\r\n \r\nこれから 寒く なりそうです。\r\n \r\nbây giờ trở đi có lẽ lạnh\r\n \r\n2) Aい（ ～い/）そうです\r\n \r\nAな［ な／］そうです\r\n \r\nMặc dù không chắc chắn như một sự kiện, nhưng qua quan sát bề ngoài có thể đoán được tính chất。\r\n \r\nこの 料理は 辛そうです。\r\n \r\nmón này có vẻ cay\r\n \r\n彼女は 頭が よさそうです。\r\ncô ấy có vẻ thông minh\r\nこの 机は 丈夫そうです。\r\ncái bàn này có vẻ chắc chắn\r\nChú ý: Khi muốn miêu tả cảm xúc của người khác, ta nên dùng các tính từ cảm xúc ( như れしい、かなしい、さびしいv.v…) cùng với ～そうです, vì ta chỉ có thể đoán được người ta cảm thấy thế nào thôi.\r\nミラーさんは うれしそうです。\r\nAnh Mira có vẻ hạnh phúc\r\n２. Vて 来ます\r\n1) Vて きます，có nghĩa là “đi đâu đó, làm chuyện gì đó, rồi trở về”\r\nちょっと たばこを 買って 来ます。\r\nđi mua thuốc lá một chút rồi về\r\nTrợ từ で chỉ nơi người này đi và làm gì đó (câu 10). Nhưng nếu mục đích hành động là di chuyển một vật ra khỏi một địa điểm, thì dùng trợ từ から (như trong câu 11)\r\nスーパーで 牛乳を 買って 来ます。\r\nmua sữa ở siêu thị mang về\r\n台所から コップを 取って 来ます。\r\nlấy tách trong bếp mang lại\r\n2)N（vị trí）へ 行って 来ます\r\nDùng thể て「いきます」trước「きます」để biểu thị ý nghĩa “đi đâu đó rồi trở về”。\r\nDùng khi bạn không nói rõ hành động bạn làm ở nơi bạn đến。\r\n郵便局へ 行って 来ます。\r\nđi bưu điện rồi về\r\n \r\n3)出かけて 来ます\r\nra ngoài rồi về\r\nThể て「でかけます」được dùng trước「きます」có nghĩa là “ra ngoài và trở về” được dùng khi bạn không nói rõ nơi bạn đi hoặc hành động bạn làm ở nơi đó.\r\nちょっと 出かけて 来ます。\r\ntôi ra ngoài một chút rồi/* large SQL query (2.0 KiB), snipped at 2,000 characters */
INSERT INTO `hoctiengnhat`.`gram` (`content`, `course`, `lesson`) VALUES ('１. Quá\r\n \r\nVます\r\n \r\nAい（～い／）}すぎます\r\n \r\nAな［な／］\r\n \r\n「～すぎます」biểu thị mức độ của hành động hoặc trạng thái là quá sức. Vì thế thường sử dụng chỉ những trạng thái khó chịu, không mong muốn.\r\n \r\nゆうべ お酒を 飲みすぎました。\r\n \r\ntối qua uống quá nhiều rượu\r\n \r\nこの セーターは 大きすぎます。\r\n \r\náo khoác này quá lớn (đối với tôi)\r\n \r\n「～すぎます」thuộc nhóm 2, nên chia theo nhóm này.\r\n \r\n例：のみすぎる、のみすぎ（ない）、のみすぎた\r\n \r\n最近の 車は 操作が 簡単すぎて、運転が おもしろくないです。\r\n \r\nvì xe hơi dạo này động tác quá đơn giản, nên lái xe không thú vị. (đối với tôi)\r\n \r\nいくら 好きでも、飲みすぎると、体に 悪いですよ。\r\n \r\ndù có thích bao nhiêu, uống quá nhiều thì sẽ không tốt cho cơ thể\r\n \r\n２ . Vます形 {やすいです\r\n \r\nVます形 {にくいです\r\n \r\n1)mẫu này biểu thị độ khó hoặc độ dễ thay đổi trong việc xử lý hoặc làm gì đó\r\n \r\nこの パソコンは 使いやすいです。\r\n \r\nmáy tính này dễ sử dụng.\r\n \r\n東京は 住みにくいです。\r\n \r\nTokyo khó sống.\r\n \r\n2) mẫu này biểu thị độ khó hoặc độ dễ thay đổi trong tính chất của một đồ vật hoặc con người hoặc khó hay dễ xảy ra một chuyện gì đó.\r\n \r\n白い シャツは 汚れやすいです。\r\n \r\náo trắng dễ dơ\r\n \r\n雨の 日は 洗濯物が 乾きにくいです。\r\n \r\nngày mưa đồ giặt khó khô\r\nChú ý:「～やすい」「～にくい」cách dùng giống như các tính từ い khác。\r\nこの 薬は 砂糖を 入れると、飲みやすく なりますよ。\r\nthuốc này nếu thêm đường vào thì sẽ (trở nên) dễ uống\r\nこの コップは 割れにくくて、安全ですよ。\r\ntách này khó vỡ, nên an toàn đấy\r\n \r\n３ . Aい（～い／） → ～く }します\r\nAな［な／］ → に }します\r\nN に }します\r\nTrong khi ～く／に なります đã học trong bài 19 cho biết một cái gì đó “chuyển sang” một tình trạng nào đó, thì ～く／～にします, trong các ví dụ 11, 12, 13 cho biết ai đó “chuyển” cái gì đó sang một tình trạng nào đó.\r\n音を 大きく します。\r\nvặn lớn âm thanh\r\n部屋を きれいに します。\r\ndọn phòng sạch sẽ\r\n塩の 量を 半分に しました。\r\nchia đôi/giảm nửa lượng muối\r\n４. Nに します\r\nBiểu thị quyết định hoặc lựa chọn\r\n部屋は シングルに しますか、ツインに しますか。\r\nBạn muốn phòng đơn hay phòng đôi?\r\n会議は あしたに します。\r\ntôi quyết định mai họp\r\n５ . Aい（ ～い） →く }動詞\r\/* large SQL query (2.2 KiB), snipped at 2,000 characters */
INSERT INTO `hoctiengnhat`.`gram` (`content`, `course`, `lesson`) VALUES ('Ｖじしょけい／Vた 　　　　　　　　ばあいは、～ 　　Trường hợp ~\r\nＶない\r\nＡい 　　\r\nＡな\r\nＮの\r\nÝ nghĩa gần giống với 「～たら」nhưng 「～ばあいは」được sử dụng nhiều khi giả định việc không tốt, việc hiếm khi có sẽ xảy ra.\r\nれい：\r\nかいしゃに　おくれる　ばあいは、れんらくして　ください。\r\nTrường hợp trễ làm thì hãy liên lạc.\r\nかぜの　くすりを　のんだ　ばあいは、ぜったいに　くるまをうんてんしないで　ください。\r\nTrường hợp đã uống thuốc cảm thì tuyệt đối đừng lái xe.\r\nでんわばんごうが　わからない　ばあいは、104に　でんわして　ください。\r\nTrường hợp không biết số điện thoại thì hãy gọi đến số 104.\r\nじしんの　ばあいは、どうしたら　いいですか。\r\nTrường hợp động đất thì nên làm thế nào.\r\nII.\r\nＶ~ふつうけい\r\nＡい　ふつうけい　　　　\r\nＡ（な）だ　―＞な　 　　　　　　のに、～　　　　　　 ~ thế mà\r\nＮだ　―＞　な\r\n「～のに」được sử dụng khi kết quả xảy ra ở vế sau trái ngược với kết quả được dự đoán từ sự thật đã được nêu ra ở vế trước. Vế sau thể hiện tâm trạng bất mãn hay bất ngờ.\r\nれい：\r\n2じかんも　まったのに、かのじょがきませんでした。\r\nĐã đợi 2 tiếng đồng hồ thế mà cô ấy không đến.\r\nしごとは　いそがしいのに、きゅうりょうは　やすいです。\r\nCông việc bận rộn thế mà lương lại rẻ.\r\nやすみなのに、はたらかなければなりません。\r\nLà ngày nghỉ thế mà phải làm việc.', 'n4', 45);
INSERT INTO `hoctiengnhat`.`gram` (`content`, `course`, `lesson`) VALUES ('I.\r\nＶ　じしょけい\r\nＶ ている　　　　　　　　 ところです\r\nＶ た\r\nところở đây không chì nơi chốn như ở bài 8 mà là thời điểm, dùng để nhấn mạnh vị trí thời gian..\r\n+　Ｖじしょけい＋ところです\r\nDùng chỉ thời điểm ngay trước khi 1 động tác nào đó được bắt đầu\r\nThường dùng chung với các từ chỉ thời gian như: これから、（ちょうど）いまから\r\nれい：\r\nＡ：ひるごはんは　もう　たべましたか。\r\nＢ：いいえ、これから　たべる　ところです。\r\n+　Ｖている＋ところです\r\nDùng chỉ thời điểm ngay khi 1 động tác nào đó đang được tiến hành\r\nThường dùng với いま\r\nれい：\r\nＡ：コピーは　もう　できましたか\r\nＢ：いいえ、いま　やっている　ところです。\r\n+　Ｖた＋ところです\r\nDùng chỉ thời điểm ngay sau khi 1 động tác nào đó được kết thúc.\r\nThường dùng chung với たったいま\r\nれい：\r\nＡ：しごとは　もう　おわりましたか。\r\nＢ：はい、たったいま　おわった　ところです。\r\n* Lưu ý:\r\na) Mẫu câu này không sử dụng khi bắt 1 câu chuyện mà được sử dụng để trả lời câu hỏi, lời kêu gọi củ đối phương nhằm giải thích tình trạng ở phía mình.\r\nれい：\r\nかいぎは　まだ　つづいている　ところですか。×\r\nＡ：かいがは　まだ　つづいていますか。\r\nＢ：いいえ、たったいま　おわったところです。○\r\nb) Những hiện tượng tự nhiên như: mưa, gió, tuyết,… không làm chủ ngữ của mẫu câu này.\r\nれい：\r\nあめが　ふっている　ところです。×\r\nあめが　ふっています。○\r\nc) Những động từ chỉ trạng thái kết quả như: けっこんします、しまります、こわれます、しります cũng không thể sử dụng\r\nれい：\r\nＡさんは　けっこんしている　ところです。×\r\nＡさんは　けっこんしています。○\r\nII. Ｖた　ばかりです\r\nThể hiện tâm trạng của người nói về thời gian không lâu sau khi 1 động tác nào đó được tiến hành.\r\nThường dùng chung với các trạng từ chỉ thời gian trong quá khứ\r\n「たところ」và「たばかり」 đều chỉ thời gian ngay sau khi 1 động tác nào đó được tiến hành. Tuy nhiên ở 「たばかり」 thì cách suy nghĩ về thời gian dựa trên ý thức, tâm lý của người nói.\r\nれい：\r\nせんげつ　かいしゃに　はいった　ばかりですから、まだ　しごとに　なれていません。\r\nVì vừa mới vào công ty tháng trước nên vẫn chưa quen với công việc.\r\nさっき　たべた　ばかりですから、まだ　おなかが　いっぱいです。\r\nVì vừa mới ăn lúc nãy nê/* large SQL query (2.7 KiB), snipped at 2,000 characters */
INSERT INTO `hoctiengnhat`.`gram` (`content`, `course`, `lesson`) VALUES ('I.\r\nＶ\r\nＡい　　　　　ふつうけい＋そうです　　　　Nghe nói là~\r\nＡ（な）\r\nＮ\r\nNgười nói truyền đạt đến người nghe thông tin mà mình thu thập được.\r\nれい：\r\nＡ：どこで　じこが　あったんですか。\r\nỞ đâu đã xảy ra tai nạn vậy?\r\nＢ：えきの　ちかくで　あったそうです。\r\nNghe nói là đã xảy ra ở gần nhà ga.\r\nII. Ｎに　よると、～そうです　　　　Theo N thì ~\r\nNgười nói chỉ rõ nguồn mang lại thông tin chẳng hạn như dự báo thời tiết, báo, tạp chí, thư, dữ liệu…\r\nれい：\r\nてんきよほうに　よると、あしたは　つよいかぜが　ふくそうです。\r\nTheo dự báo thời tiết thì ngày mai sẽ có gió mạnh thổi.\r\nニュースに　よると、ほっかいどうで　ひこうきが　おちたそうです。\r\nTheo tin tức thời sự thì ở Hokkaido đã có máy bay rơi.\r\nしんぶんに　よると、ことしは　こうつうじこが　おおかったそうです。\r\nTheo báo chí thì năm nay tai nạn giao thông nhiều.\r\nたなかさんの　はなしに　よると、すずきさんは　えいごの　せんせいだそうです。\r\nTheo lời anh Tanaka thì co Suzuki la giáo viên tiếng Anh.\r\nIII.\r\nＶ（ふつうけい）\r\nＡい（ふつうけい） ＋　ようです hình như là, dường như là ~\r\nＡ（な）だ　–>　な\r\nＮ だ –> の\r\nNgười nói đưa ra suy đoán, phán đoán mang tính chủ quan có được dựa vào điều mình nắm bắt được cảm giác, tri giác.\r\nれい：\r\nかちょうは　じむしょに　いないようです。\r\nHình như là trưởng phòng không có ở văn phòng.\r\nそとは　さむいようです。\r\nDường như là bên ngoài đang lạnh.\r\nＡ：へんな　においが　しますね。\r\nCó mùi gì lạ nhỉ.\r\nＢ：ええ、なにか　もえて　いるようです。\r\nỪ, hình như là có gì đó đang cháy.', 'n4', 47);
INSERT INTO `hoctiengnhat`.`gram` (`content`, `course`, `lesson`) VALUES ('Phần 1: Cách chia động từ ở thể sai khiến\r\nCấu trúc:\r\nV nhóm I: V「う]→V「あ」せる\r\n　　 Vう→Vわせる\r\nvd: ふむ→ふませる\r\nかく→かかせる\r\nいう→いわせる\r\n\r\nV nhóm II：　Vる→Vさせる\r\nたべる→たべさせる\r\nみる→みさせる\r\n\r\nV nhóm III: する→しさせる\r\nくる→こさせる\r\n\r\nPhần 2: V使役形: bắt, cho phép làm, khiến cho.... \r\nCấu trúc: \r\nĐối với các động từ không đi cùng với trợ từ を\r\nVí dụ như: 行く、出張する、休む、寝る hoặc các động từ chỉ cảm xúc như 心配する、安心する、笑う、泣く。。。\r\n～はＮをV使役形\r\nVí dụ:\r\nお母さんは娘さんを寝かせます。\r\nMẹ dỗ con gái ngủ.\r\n私は姉を笑わせました。\r\nTôi làm chị gái cười.\r\n\r\nĐối với các động từ đi cùng trợ từ を\r\n～はＮ１にをＮ２をＶ使役形\r\n\r\nVí dụ:\r\n父は私に魚を食べさせます。\r\nBố bắt tôi ăn cá.\r\n私は子供に部屋ほ掃除させる\r\nTôi bắt con dọn dẹp phòng.\r\n\r\nPhần 3: ~ V使役形て　いただけませんか：có thể nào cho phép tôi...\r\nCấu trúc:\r\n~ Vても　いいですか。\r\n~ Vて　もらえませんか。\r\n~ V使役形て　いただけませんか。\r\nÝ nghĩa: xin phép được làm một việc gì đó cách lịch sự, các mẫu câu xếp theo mức độ lịch sự tăng dần.\r\nVí dụ:\r\nＡ：部長、来週、友達が結婚するんでんが二日間を休ませて　いただけませんか。\r\nThưa trưởng phòng, tuần tới vì người bạn kết hôn nên tôi xin phép nghỉ 2 ngày được không ạ?\r\nＢ：そうでんか。いいでんよ。\r\nVậy à, được thôi.\r\nA: すむません、しばらくここに車を止めさせていただけませんか。荷物を降ろしますので\r\nXin lỗi, có thể dừng xe ở đây một lát được không ạ? Vì tôi làm rơi hành lý rồi.\r\nB: いいですよ。\r\nĐược thôi.', 'n4', 48);
INSERT INTO `hoctiengnhat`.`gram` (`content`, `course`, `lesson`) VALUES ('Ở bài 49 và 50 chúng ta sữ học về けいご. けいご là cách nói thể hiện sự kính trọng của người nói đối với người nghe hoặc người được nói tới. Người nói dùng けいご đối với người mà theo quan hệ xã hội thì mình phải biểu thị sự kính trọng. Việc dùng けいご tùy thuộc vào 3 yếu tố sau:\r\n(1) Khi người nói ở vị trí thấp hơn về mặt tuổi tác hoặc địa vị xã hội, thì người nói dùng けいご để biểu thị sự kính trọng của mình với người nghe có vị trí cao hơn mình.\r\n(2) Trong những trường hợp người nói có quan hệ không thân lắm với người nghe , ví dụ như khi mới gặp nhau lần đầu, thì người nói dùng けいご để biểu thị sự kính trọng của mình với người nghe.\r\n(3) Quan hệ "ウチ (bên trong) - ソト (bên ngoài)" cũng là yếu tố cần phải suy tính tới khi dùng けいご.\r\nKhái niệm "ウチ" chỉ những người thuộc cùng nhóm với mình ví dụ như gia đình, công ty v.v.., còn khái niệm "ソト" chỉ những người nằm ngoài nhóm. Khi người nói nói với そとのひと (người "bên ngoài") về ウチのひと(người "bên trong") thì người "bên trong" này có vị trí tương đương với người nói. Vì thế, cho dù người bên trong có vị trí cao hơn, nhưng người nói không dùng けいご như khi nói với người đó\r\n2.\r\nCác loại 敬語\r\nCó ba loại けいご là そんけいご (tôn kính ngữ), けんじょうご (khiêm nhường ngữ) và ていねいご (thể lịch sự). Ở bài 49 này chúng ta sẽ học về そんけいご.\r\n3.\r\n尊敬語 (tôn kính ngữ)\r\nそんけいご là cách nói được dùng để biểu thị sự kính trọng của người nói đối với người nghe hoặc người được nói tới, khi nói về hành vi hoặc trạng thái của người nghe hoặc người được nói tới. Cách nói này cũng được dùng khi nói về những đồ vật, sự việc liên quan đến người nghe.\r\n1) Động từ\r\n(1) Động từ tôn kính\r\nĐộng từ ở thể bị động có thể được dùng làm động từ tôn kính. Những động từ này thuộc nhóm II\r\n中村さんは　７時に　来られます。\r\nAnh Nakamura đến vào lúc 7 giờ.\r\n1\r\nお酒を　やめられたんですか。\r\nAnh/ Chị đã bỏ rượu à?\r\n2\r\n(2) お Động từ thể ます　に　なります\r\nCách nói này có mức độ tôn kính cao hơn cách nói ở phần (1). Đố/* large SQL query (4.8 KiB), snipped at 2,000 characters */
INSERT INTO `hoctiengnhat`.`gram` (`content`, `course`, `lesson`) VALUES ('1.\r\n謙譲語 (khiêm nhường ngữ)\r\nけんじょうご là cách nói hạ mình (khiêm nhường) mà người nói dùng để nói về hành vi của bản thân mình qua đó thể hiện sự kính trọng của mình đối với người nghe hoặc người được nói tới. Đối tượng để thể hiện sự kính trọng là người trên hoặc ソトの　ひと ( người "bên ngoài"). Ngoài ra, người nói cũng dùng けんじょうご khi nói với ソトの　ひと ( người "bên ngoài") về ウチの　ひと ( người "bên trong").\r\n1)\r\nお/ご～します\r\n(1) お Động từ (nhóm I,II) thể ます　します\r\n重そうですね。お持ちしましょうか。\r\nTrông có vẻ nặng quá nhỉ. Tôi giúp anh/ chị nhé?\r\n1\r\n私が　社長に　スケジュールを　お知らせします。\r\nTôi thông báo lịch làm việc tới giám đốc.\r\n2\r\n兄が　車で　お送りします。\r\nAnh Trai tôi sẽ chở anh/chị bằng ô tô.\r\n3\r\nNgười nói dùng cách nói khiêm nhường để nói với người nghe ở ví dụ 1, và với người được nói tới ở ví dụ 2. Còn ở ví dụ 3 thì chủ thể của hành vi không phải là người nói, nhưng là 「ウチのひと」 (người "bên trong") thuộc cùng một nhóm với người nói.\r\nCần chú ý là cách nói này không dùng với các động từ mà thể ます có một âm tiết, ví dụ như 「みます」, 「います」\r\n(2) ご Động từ (nhóm III)\r\n江戸東京博物館へ　ご案内します。\r\nChúng tôi sẽ dẫn các vị đến bảo tàng Edo-Tokyo.\r\n4\r\nきょうの　予定を　ご説明します。\r\nTôi sẽ giải thích về lịch làm việc ngày hôm nay.\r\n5\r\nCách nói này được dùng với động từ thuộc nhóm III. Ngoài những động từ được dùng ở các ví dụ trên còn có các động từ khác như 「しょうかいします」, 「しょうたいします」, 「そうだんします」, 「れんらくします」 v.v.. Tuy nhiên đối với trường hợp ngoại lệ là các động từ như 「でんわします」, 「やくそくします」 v.v.. thì chúng ta không dùng 「ご」 mà dùng 「お」 ở trước động từ.\r\nChú y: Mẫu câu ở (1) và (2) chỉ được dùng khi có đối tượng tiếp nhận hành vì ( ngoài người nói ra), và người nói muốn bày tỏ sự kính trọng của mình đối với đối tượng này. Ở ví dụ dưới đây, khi không có đối tượng tiếp nhaanjj hành vi thì chúng ta không dùng mẫu câu này.\r\nKhông dùng 私は　来月　国へ　お帰りします。\r\n2)\r\nĐộng từ khiêm nhường đặc biệt\r\nCó một số động từ mà bản thân chúng đã mang sắc 